package br.com.generation.variaveis;

public class variavel02 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x=5;
		int y=10;
		int resultado;
		
		resultado=x+y;
		System.out.println(resultado);
		
		resultado=x-y;
		System.out.println(resultado);
		
		resultado=x*y;
		System.out.println(resultado);
		
		resultado=y/x;
		System.out.println(resultado);
		
		resultado=y%x;
		System.out.println(resultado);
	}

}
