package br.com.generation.variaveis;

public class variavel03 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int var1=10;
		double var2=10.0;
		
		System.out.println("Valor original da primeira variavel é: "+var1);
		System.out.println("Valor original da segunda variavelé: "+var2);
		
		var1=var1/4;
		var2=var2/4;
		
		System.out.println("Valor alterado da primeira variavel é: "+var1);
		System.out.println("Valor alterado da segunda variavel é:  "+var2);
	}

}
